import React, { FC } from 'react'
import { Draggable } from 'react-beautiful-dnd'
import { User } from 'main/types' // Adjust the import path as necessary
import Users from './Users' // Adjust the import path as necessary
import IconSVG from './inlineImages' // Adjust the import path as necessary

interface CardProps {
  id: string;
  index: number;
  text: string;
  users: User[];
  assignedUser: User | null;
  onUserSelect: (cardId: string, user: User) => Promise<void>;
  onDeleteCard: (cardId: string) => Promise<void>;
}

const Card: FC<CardProps> = ({
  id,
  index,
  text,
  users,
  assignedUser,
  onUserSelect,
  onDeleteCard
}) => {
  const [showUserList, setShowUserList] = React.useState(false)

  return (
    <Draggable draggableId={id} index={index}>
      {(provided) => (
        <div
          ref={provided.innerRef}
          {...provided.draggableProps}
          {...provided.dragHandleProps}
          className="mb-2 flex flex-col rounded bg-white p-2 shadow"
        >
          <div className="flex items-center justify-between">
            <p>{text}</p>
            <div className="flex items-center">
              <button
                onClick={() => setShowUserList(!showUserList)}
                className="mr-2"
              >
                <IconSVG.Person />
              </button>
              <button
                onClick={() => onDeleteCard(id)}
                className="text-xs text-gray-500 hover:text-gray-700"
              >
                Delete
              </button>
            </div>
          </div>
          {showUserList && (
            <Users
              cardId={id}
              users={users}
              showUserList={showUserList}
              toggleUserList={() => setShowUserList(!showUserList)}
              onUserSelect={onUserSelect}
            />
          )}
        </div>
      )}
    </Draggable>
  )
}

export default Card
