import { type FC, useState } from 'react'
import { Draggable } from 'react-beautiful-dnd'
import { type User } from 'main/types' 
import Users from './Users' 
import IconSVG from './inlineImages' 

interface CardProps {
  id: string;
  index: number;
  text: string;
  users: User[];
  assignedUser: User | null;
  onUserSelect: (cardId: string, user: User) => Promise<void>;
  onDeleteCard: (cardId: string) => Promise<void>;
}

const Card: FC<CardProps> = ({
  id,
  index,
  text,
  users,
  assignedUser,
  onUserSelect,
  onDeleteCard
}) => {
  const [showUserList, setShowUserList] = useState(false)

  const handleUserSelect = async (cardId: string, user: User) => {
    await onUserSelect(cardId, user)
    setShowUserList(false) // Close the user list after selecting a user
  }

  return (
    <Draggable draggableId={id} index={index}>
      {(provided) => (
        <div
          ref={provided.innerRef}
          {...provided.draggableProps}
          {...provided.dragHandleProps}
          className="mb-2 flex flex-col space-y-2 rounded bg-white p-2 shadow"
        >
          <div className="flex items-center justify-between">
            <p>{text}</p>
