import { useEffect, useState } from 'react'
import { type  DropResult } from 'react-beautiful-dnd'
import { useMutation, useQuery } from '@apollo/client'
import { GET_COLUMNS, ADD_CARD, MOVE_CARD, DELETE_CARD, CHANGE_USER } from '../graphql/queries'
import { type User, type BoardData, type Column } from './types'

export interface UseTaskFlowReturn {
  loading: boolean
  error: Error | undefined
  columns: Column[] | undefined
  cardTextByColumn: Record<string, string>
  draggedCard: string | null
  users: User[]
  setNewCardText: (text: string) => void
  setDraggedCard: (cardId: string | null) => void
  handleAddCard: (columnId: string) => Promise<void>
  handleDragEnd: (result: any) =>  Promise<void>
  handleDeleteCard: (cardId: string) => Promise<void>
  assignUserToCard: (cardId: string, userId: string) => Promise<void>
  setCardTextByColumn: (text: string) => void
}

const useTaskFlow = (): UseTaskFlowReturn => {
  const { loading, error, data } = useQuery<BoardData>(GET_COLUMNS)
  const [columns, setColumns] = useState<Column[]>([])
  const [addCardMutation] = useMutation(ADD_CARD)
  const [moveCardMutation] = useMutation(MOVE_CARD)
  const [deleteCardMutation] = useMutation(DELETE_CARD)
  const [changeUserMutation] = useMutation(CHANGE_USER)
  const [newCardText, setNewCardText] = useState('')
  const [draggedCard, setDraggedCard] = useState<string | null>(null)
  const [users, setUsers] = useState<User[]>([])
  const [cardTextByColumn, setCardTextByColumn] = useState({})

  const handleAddCard = async (columnId: string) => {
    await addCardMutation({
      variables: { columnId, text: newCardText },
      update: (cache, { data: { addCard } }) => {
        const existingData = cache.readQuery<{ getColumns: Column[] }>({ query: GET_COLUMNS })
        if (!existingData) return

        const newColumns = existingData.getColumns.map(column => {
          if (column.id === columnId) {
            return { ...column, cards: [...column.cards, addCard] }
          }
          return column
        })

        cache.writeQuery({
          query: GET_COLUMNS,
          data: { getColumns: newColumns }
        })
      }
    })
    setNewCardText('')
  }

  const handleDeleteCard = async (cardId: string) => {
    await deleteCardMutation({
      variables: { cardId },
      update: (cache) => {
        // Read the current state of the columns from the cache
        const existingData = cache.readQuery<{ getColumns: Column[] }>({ query: GET_COLUMNS })
        if (!existingData) return

        // Clone the existing columns to avoid direct mutation
        const newColumns = existingData.getColumns.map(column => {
          // Filter out the deleted card from each column's cards
          const filteredCards = column.cards.filter(card => card.id !== cardId)
          return { ...column, cards: filteredCards }
        })

        // Write the updated columns back to the cache
        cache.writeQuery({
          query: GET_COLUMNS,
          data: { getColumns: newColumns }
        })
      }
    })
  }

  const handleDragEnd = async (result: DropResult) => {
    const { draggableId, source, destination } = result

    // Check if there's a destination and the card is moved to a new place
    if (!destination || (destination.droppableId === source.droppableId && destination.index === source.index)) {
      return // Do nothing if there's no destination or the card was dropped back to its original place
    }

    await moveCardMutation({
      variables: {
        cardId: draggableId,
        newColumnId: destination.droppableId
      },
      optimisticResponse: {
        moveCard: {
          __typename: 'Card',
          id: draggableId,
          columnId: destination.droppableId
        }
      },
      update: (cache) => {
        const existingData = cache.readQuery<{ getColumns: Column[] }>({ query: GET_COLUMNS })
        if (!existingData) return

        // Cloning the existing columns to avoid direct mutation
        const newColumns = existingData.getColumns.map(column => ({ ...column, cards: [...column.cards] }))

        // Find and remove the card from its original column
        const sourceColumnIndex = newColumns.findIndex(column => column.id === source.droppableId)
        const cardIndex = newColumns[sourceColumnIndex].cards.findIndex(card => card.id === draggableId)
        const [movedCard] = newColumns[sourceColumnIndex].cards.splice(cardIndex, 1)

        // Add the card to its new column
        const destinationColumnIndex = newColumns.findIndex(column => column.id === destination.droppableId)
        newColumns[destinationColumnIndex].cards.splice(destination.index, 0, movedCard)

        // Write the updated columns back to the cache
        cache.writeQuery({
          query: GET_COLUMNS,
          data: { getColumns: newColumns }
        })
      }
    })
  }

  const assignUserToCard = async (cardId: string, userId: string) => {
    await changeUserMutation({
      variables: { cardId, assignedUser: userId }
      // Cache update logic here
    })
  }

  useEffect(() => {
    if (!loading && data) {
      setColumns(data.getColumns || [])
      setUsers(data.getUsers || [])
    }
  }, [loading, data])

  return {
    loading,
    error,
    columns,
    cardTextByColumn,
    draggedCard,
    users,
    setCardTextByColumn,
    setNewCardText,
    setDraggedCard,
    handleAddCard,
    handleDragEnd,
    handleDeleteCard,
    assignUserToCard
  }
}

export default useTaskFlow
