import { useState, type  FC } from 'react'
import debounce  from 'lodash-es/debounce'
import { User } from 'main/types' // Adjust the import path as necessary
import IconSVG from './inlineImages'
import { AVATAR_COLORS, DEFAULT_AVATAR_COLOR } from 'main/constants'
import generateColorForString from 'main/utils/generateColorForString'

interface UsersProps {
  cardId: string;
  users: User[];
  showUserList: boolean;
  toggleUserList: () => void;
  onUserSelect: (cardId: string, user: User) => Promise<void>;
}

const Users: FC<UsersProps> = ({ cardId, users, showUserList, toggleUserList, onUserSelect }) => {
  const [searchTerm, setSearchTerm] = useState('')
  const [selectedUser, setSelectedUser] = useState<User | null>(null)

  const handleUserClick = async (user: User) => {
    await onUserSelect(cardId, user)
    setSelectedUser(user)
    toggleUserList() // Close the user list after selecting a user
  }

  const filteredUsers = users.filter(user =>
    user.name.toLowerCase().includes(searchTerm.toLowerCase())
  )

  const debouncedSetSearchTerm = debounce((value) => {
    setSearchTerm(value)
  }, 300)

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    debouncedSetSearchTerm(event.target.value)
  }

  return (
    <div className="relative">
      <button onClick={toggleUserList} className="outline-none focus:outline-none">
        <IconSVG.Person color={generateColorForString(selectedUser?.name ?? '', AVATAR_COLORS, DEFAULT_AVATAR_COLOR)} />
      </button>

      {showUserList && (
        <div className="absolute right-0 z-10 mt-2 w-48 rounded-md border bg-white shadow-lg">
          <div className="border-b p-2">
            <input
              type="text"
              placeholder="Search users..."
              onChange={handleChange}
              className="w-full rounded-md border px-2 py-1 text-sm focus:outline-none"
            />
          </div>
          <ul className="max-h-60 overflow-y-auto">
            {filteredUsers.map(user => (
              <li
                key={user.id}
                className="flex cursor-pointer items-center px-2 py-1 hover:bg-gray-100"
                onClick={() => handleUserClick(user)}
              >
                <IconSVG.Person color={generateColorForString(user.name, AVATAR_COLORS, DEFAULT_AVATAR_COLOR)} />
                <span className="ml-2 text-sm">{user.name}</span>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  )
}

export default Users
