import { useEffect, useState } from 'react'
import { useMutation, useQuery } from '@apollo/client'
import { GET_COLUMNS, ADD_CARD, MOVE_CARD, DELETE_CARD, CHANGE_USER } from '../graphql/queries'
import { type User, type BoardData, type Column, type Card } from './types'

export interface UseTaskFlowReturn {
  loading: boolean;
  error: Error | undefined;
  columns: Column[] | undefined;
  newCardText: string;
  draggedCard: string | null;
  users: User[];
  setNewCardText: (text: string) => void;
  setDraggedCard: (cardId: string | null) => void;
  handleAddCard: (columnId: string) => Promise<void>;
  handleDragEnd: (result: any) => void;
  handleDeleteCard: (cardId: string) => Promise<void>;
  assignUserToCard: (cardId: string, userId: string) => Promise<void>;
}

const useTaskFlow = (): UseTaskFlowReturn => {
  const { loading, error, data } = useQuery<BoardData>(GET_COLUMNS)
  const [columns, setColumns] = useState<Column[]>([])
  const [addCardMutation] = useMutation(ADD_CARD)
  const [moveCardMutation] = useMutation(MOVE_CARD)
  const [deleteCardMutation] = useMutation(DELETE_CARD)
  const [changeUserMutation] = useMutation(CHANGE_USER)
  const [newCardText, setNewCardText] = useState('')
  const [draggedCard, setDraggedCard] = useState<string | null>(null)
  const [users, setUsers] = useState<User[]>([])

  const handleAddCard = async (columnId: string) => {
    await addCardMutation({
      variables: { columnId, text: newCardText },
      update: (cache, { data: { addCard } }) => {
        const existingData = cache.readQuery<BoardData>({ query: GET_COLUMNS })
        const newColumns = existingData?.columns.map(column => {
          if (column.id === columnId) {
            return { ...column, cards: [...column.cards, addCard] }
          }
          return column
        })
        cache.writeQuery({
          query: GET_COLUMNS,
          data: { getColumns: newColumns }
        })
      }
    })
    setNewCardText('')
  }

  const handleDeleteCard = async (cardId: string) => {
    await deleteCardMutation({
      variables: { cardId }
    })
  }

  const handleDragEnd = async (result) => {
    const { draggableId: cardId, source, destination } = result
    if (!destination || (destination.droppableId === source.droppableId && destination.index === source.index)) {
      // No movement happened
      return
    }

    // Assuming destination.droppableId represents the newColumnId
    const newColumnId = destination.droppableId

    await moveCardMutation({
      variables: { cardId, newColumnId },
      optimisticResponse: true, // Optional: for immediate UI response
      update: (cache) => {
        // Update cache here if necessary
      }
    })
  }
  const assignUserToCard = async (cardId: string, userId: string) => {
    await changeUserMutation({
      variables: { cardId, assignedUser: userId }
      // Cache update logic here
    })
  }

  useEffect(() => {
    if (!loading && data) {
      setColumns(data.getColumns)
      setUsers(data.getUsers)
    }
  }, [loading, data])

  return {
    loading,
    error,
    columns,
    newCardText,
    draggedCard,
    setNewCardText,
    setDraggedCard,
    handleAddCard,
    handleDragEnd,
    handleDeleteCard
  }
}

export default useTaskFlow
