import { type FC } from 'react'
import type React from 'react'
import { useState } from 'react'
import { type User } from 'main/types'
import IconSVG from './inlineImages'
import { AVATAR_COLORS, DEFAULT_AVATAR_COLOR } from 'main/constants'
import generateColorForString from 'main/utils/generateColorForString'

export interface UsersProps {
  cardId: string
  users: User[]
  showUserList: boolean
  toggleUserList: () => void
  onUserSelect: ({ cardId, user }: {
    cardId: string
    user: User
  }) => Promise<void>
}

const Users: FC<UsersProps> = ({ cardId, users, showUserList, toggleUserList, onUserSelect }) => {
  const [searchTerm, setSearchTerm] = useState('')
  const [selectedUser, setSelectedUser] = useState<User | null>(null)
  const filteredUsers = users.filter(user =>
    user.name.toLowerCase().includes(searchTerm.toLowerCase())
  )

  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>): void => {
    if (e.key === 'Enter') {
      setSearchTerm(e.currentTarget.value.trim())
    }
  }

  const handleUserClick = (user: User): void => {
    void onUserSelect({ cardId, user })
    setSelectedUser(user)
    toggleUserList() // Close the user list after selecting a user
  }

  return (
    <div className="relative">
      <button onClick={toggleUserList} className="outline-none focus:outline-none">
        <IconSVG.Person color={generateColorForString(selectedUser?.name ?? '', AVATAR_COLORS, DEFAULT_AVATAR_COLOR)} />
      </button>

      {showUserList && (
        <div className="absolute right-0 mt-2 w-48 rounded border bg-white shadow-lg">
          <div className="border-b p-2">
            <input
              type="text"
              placeholder="Search users"
              value={searchTerm}
              onChange={(e) => {
                setSearchTerm(e.target.value) 
              }}
              onKeyDown={handleKeyDown}
              className="w-full rounded border px-2 py-1 focus:outline-none"
            />
          </div>
          <ul>
            {filteredUsers.map(user => (
              <li key={user.id} className=" px-2 py-1 hover:bg-gray-100">
                <button
                  onClick={() => {
                    handleUserClick(user) 
                  }}
                  className="flex size-full basis-5 flex-nowrap outline-none focus:outline-none">
                  <IconSVG.Person color={generateColorForString(user.name, AVATAR_COLORS, DEFAULT_AVATAR_COLOR)} />
                  <p className='grow justify-self-start'>{user.name}</p>
                </button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  )
}

export default Users
