import React from 'react'
import { DragDropContext } from 'react-beautiful-dnd'
import Column from 'main/components/Column' // Adjust this import path as necessary
import useTaskFlow from 'main/hook' // Adjust the import path as necessary

const TaskFlow: React.FC = () => {
  const {
    loading,
    error,
    columns,
    newCardText,
    setNewCardText,
    handleAddCard,
    handleDeleteCard,
    users,
    handleDragStart,
    handleDragEnd,
    setDraggedCard
  } = useTaskFlow()

  if (loading) return <p>Loading...</p>
  if (error) return <p>Error: {error.message}</p>

  return (
    <DragDropContext onDragStart={(start) => setDraggedCard(start.draggableId)} onDragEnd={handleDragEnd}>
      <div className="flex">
        {columns.map((column) => (
          <Column
            key={column.id}
            id={column.id}
            title={column.title}
            column={column}
            onDrop={() => {}}
          >
            {/* Column's children (cards) will be rendered here, possibly using a Droppable wrapper */}
            <input
              type="text"
              placeholder="Add new card"
              value={newCardText}
              onChange={(e) => setNewCardText(e.target.value)}
              className="w-full rounded border border-gray-200 p-2"
            />
            <button
              onClick={() => handleAddCard(column.id)}
              className="mt-2 rounded bg-blue-500 px-4 py-2 text-lg text-white hover:bg-blue-600"
            >
              Add Card
            </button>
          </Column>
        ))}
      </div>
    </DragDropContext>
  )
}

export default TaskFlow
