import { FC, useState } from 'react'
import { Draggable } from 'react-beautiful-dnd'
import UsersList from './UsersList'
import IconSVG from './inlineImages'
import { User } from 'main/types'
import generateColorForString from 'main/utils/generateColorForString'
import { AVATAR_COLORS, DEFAULT_AVATAR_COLOR } from 'main/constants'

interface CardProps {
  id: string
  index: number
  text: string
  assignedUser: User | null
  users: User[]
  onUserSelect: (cardId: string, user: User) => Promise<void>
  onDeleteCard: () => Promise<void>
}

const Card: FC<CardProps> = ({
  id,
  index,
  text,
  assignedUser,
  users,
  onUserSelect: _onUserSelect,
  onDeleteCard
}) => {
  const [showUsersList, setShowUsersList] = useState(false)
  const [showOptionsMenu, setShowOptionsMenu] = useState(false)

  const onUserSelect = (user: User) => {
    void _onUserSelect(id, user)
    setShowUsersList(!showUsersList)
  }

  return (
    <Draggable draggableId={id} index={index}>
      {(provided) => (
        <div
          ref={provided.innerRef}
          {...provided.draggableProps}
          {...provided.dragHandleProps}
          className="mb-2 flex flex-col space-y-2 rounded bg-white p-2 shadow"
        >
          <div className="flex items-center justify-between">
            <p>{text}</p>
            <div className="flex items-center space-x-2">
              <button onClick={() => setShowUsersList(!showUsersList)} className="outline-none focus:outline-none">
                <IconSVG.Person color={generateColorForString(assignedUser?.name ?? '', AVATAR_COLORS, DEFAULT_AVATAR_COLOR)} />
              </button>
              <button onClick={() => setShowOptionsMenu(!showOptionsMenu)} className="outline-none focus:outline-none">
                <IconSVG.ThreeDots />
              </button>
            </div>
          </div>

          {showUsersList && (
            <div className="absolute mt-2 rounded border bg-white p-2 shadow">
              <UsersList users={users} onUserSelect={onUserSelect}/>
            </div>
          )}

          {showOptionsMenu && (
            // <div className="absolute right-0 mt-8 rounded border bg-white p-2 shadow">
            <ul className="text-sm">
              <li className="cursor-pointer p-2 hover:bg-gray-100" onClick={() => onDeleteCard()}>Delete</li>
            </ul>
            // </div>
          )}
        </div>
      )}
    </Draggable>
  )
}

export default Card
