import { DragDropContext, Droppable } from 'react-beautiful-dnd'
import Column from 'main/components/Column' 
import Card from 'main/components/Card' 
import useTaskFlow from 'main/hook'

const TaskFlow: React.FC = () => {
  const {
    loading,
    error,
    columns,
    users,
    cardTextByColumn,
    setCardTextByColumn,
    onAddCard,
    onDeleteCard,
    onDragEnd,
    setDraggedCard,
    onUserSelect
  } = useTaskFlow()

  if (loading) return <p>Loading...</p>
  if (error) return <p>Error: {error.message}</p>

  return (
    <DragDropContext onDragStart={(start) => setDraggedCard(start.draggableId)} onDragEnd={onDragEnd}>
      <div className="grid grid-flow-col grid-rows-3 gap-4"> {/* Adjusted grid definition to create three columns */}
        {columns?.map((column) => (
          <Droppable droppableId={String(column.id)} key={column.id}>
            {(provided) => (
              <div ref={provided.innerRef} {...provided.droppableProps} className="min-w-[240px] rounded bg-gray-100 p-4">
                <Column
                  key={column.id}
                  id={column.id}
                  title={column.title}
                >
                  {column.cards.map((card, index) => (
                    <Card
                      key={card.id}
                      id={card.id}
                      index={index}
                      text={card.text}
                      assignedUser={users.find(user => user.id === card.assignedUser) ?? null}
                      users={users}
                      onUserSelect={(_, user) => onUserSelect(card.id, user)}
                      onDeleteCard={() => onDeleteCard(card.id)}
                    />
                  ))}
                  {provided.placeholder}
                  <div className="mt-2">
                    <input
                      type="text"
                      placeholder="Add new card"
                      value={cardTextByColumn[column.id] || ''}
                      onChange={(e) => setCardTextByColumn({
                        ...cardTextByColumn,
                        [column.id]: e.target.value
                      })}
                      className="w-full rounded border border-gray-200 p-2"
                    />
                    <button
                      onClick={() => {
                        onAddCard(column.id, cardTextByColumn[column.id] || '')
                        setCardTextByColumn({
                          ...cardTextByColumn,
                          [column.id]: '' // Clear the input field after adding the card
                        })
                      }}
                      className="mt-2 rounded bg-blue-500 px-4 py-2 text-lg text-white hover:bg-blue-600"
                    >
              Add Card
                    </button>
                  </div>
                </Column>
              </div>
            )}
          </Droppable>
        ))}

      </div>
    </DragDropContext>
  )
}

export default TaskFlow
