import { type FC } from 'react'
import { useState } from 'react' // Import useState hook
import { Draggable } from 'react-beautiful-dnd'
import UsersList from './UsersList'
import IconSVG from './inlineImages'
import generateColorForString from 'main/utils/generateColorForString'
import Modal from './Modal' // Import the Modal component
import { AVATAR_COLORS, DEFAULT_AVATAR_COLOR } from 'main/constants'
import { User } from 'main/types'

interface CardProps {
  id: string;
  index: number;
  text: string;
  assignedUser: User | null;
  users: User[];
  onUserSelect: (cardId: string, user: User) => Promise<void>
  onDeleteCard: () => Promise<void>;
  onAddText: (text: string) => void; // Add onAddText prop
}

const Card: FC<CardProps> = ({
  id,
  index,
  text,
  assignedUser,
  users,
  onUserSelect,
  onDeleteCard,
  onAddText // Include onAddText prop
}) => {
  const [showUserList, setShowUserList] = useState(false)
  const [selectedUser, setSelectedUser] = useState<User | null>(null)
  const [isModalOpen, setIsModalOpen] = useState(false) // State to control modal visibility

  const handleUserSelect = async (user: User) => {
    await onUserSelect(id, user)
    setSelectedUser(user)
    setShowUserList(false)
  }

  return (
    <Draggable draggableId={id} index={index}>
      {(provided) => (
        <div
          ref={provided.innerRef}
          {...provided.draggableProps}
          {...provided.dragHandleProps}
          className="mb-2 flex flex-col space-y-2 rounded bg-white p-2 shadow"
        >
          <div className="flex items-center justify-between">
            <p>{text}</p>
            <div className="flex items-center space-x-2">
              <button onClick={() => setShowUserList(!showUserList)} className="outline-none focus:outline-none">
                <IconSVG.Person color={generateColorForString(selectedUser?.name ?? '', AVATAR_COLORS, DEFAULT_AVATAR_COLOR)} />
              </button>
              <button
                onClick={() => setIsModalOpen(true)}
                className="outline-none focus:outline-none"
              >
                <IconSVG.ThreeDots /> {/* Replace with your 3 dots icon */}
              </button>
            </div>
          </div>
          <div className="flex items-center space-x-2"> {/* Render user icon */}
            <UsersList
              users={users}
              showUserList={showUserList}
              onUserSelect={handleUserSelect}
            />
          </div>
          {isModalOpen && (
            <Modal
              onClose={() => setIsModalOpen(false)}
              onActionBtnClick={onDeleteCard}
              cardId={id}
              onSubmit={onAddText}
            />
          )}
        </div>
      )}
    </Draggable>
  )
}

export default Card
