import { useState, FC } from 'react'
import { Draggable } from 'react-beautiful-dnd'
import { type User } from 'main/types' // Adjust import path as necessary
import Users from './Users' // Adjust import path as necessary
import Modal from './Modal' // Adjust import path as necessary
import IconSVG from './inlineImages' // Adjust import path as necessary

interface CardProps {
  id: string;
  index: number;
  text: string;
  users: User[];
  assignedUser: User | null;
  onUserSelect: ({ cardId, user }: { cardId: string, user: User }) => Promise<void>;
  onDeleteCard: (cardId: string) => Promise<void>;
}

const Card: FC<CardProps> = ({
  id,
  index,
  text,
  users,
  assignedUser,
  onUserSelect,
  onDeleteCard
}) => {
  const [isModalOpen, setIsModalOpen] = useState(false)
  const [showUserList, setShowUserList] = useState(false)

  return (
    <Draggable draggableId={id} index={index}>
      {(provided) => (
        <div
          ref={provided.innerRef}
          {...provided.draggableProps}
          {...provided.dragHandleProps}
          className="mb-2 flex flex-col rounded bg-white p-2 shadow"
        >
          <div className="flex items-center justify-between">
            <p>{text}</p>
            <button
              onClick={() => setIsModalOpen(true)}
              className="text-xs text-gray-500 hover:text-gray-700"
            >
              <IconSVG.ThreeDots />
            </button>
          </div>
          <div className="mt-2">
            {assignedUser && <p>Assigned to: {assignedUser.name}</p>}
            <button
              onClick={() => setShowUserList(!showUserList)}
              className="text-xs text-blue-500 hover:text-blue-700"
            >
              {showUserList ? 'Hide Users' : 'Assign User'}
            </button>
            {showUserList && (
              <Users
                cardId={id}
                users={users}
                onUserSelect={onUserSelect}
              />
            )}
          </div>
          {isModalOpen && (
            <Modal
              onClose={() => setIsModalOpen(false)}
              cardId={id}
              onActionBtnClick={() => onDeleteCard(id)}
            />
          )}
        </div>
      )}
    </Draggable>
  )
}

export default Card
