import { DragDropContext, Droppable } from 'react-beautiful-dnd'
import Column from 'main/components/Column' 
import Card from 'main/components/Card' 
import useTaskFlow from 'main/hook'

const TaskFlow: React.FC = () => {
  const {
    loading,
    error,
    columns,
    cardTextByColumn,
    setCardTextByColumn,
    handleAddCard,
    handleDeleteCard,
    handleDragEnd,
    setDraggedCard
  } = useTaskFlow()

  if (loading) return <p>Loading...</p>
  if (error) return <p>Error: {error.message}</p>

  return (
    <DragDropContext onDragStart={(start) => setDraggedCard(start.draggableId)} onDragEnd={handleDragEnd}>
      <div className="flex overflow-auto">
        {columns?.map((column) => (
          <Droppable droppableId={column.id} key={column.id}>
            {(provided) => (
              <div ref={provided.innerRef} {...provided.droppableProps}>
                <Column
                  key={column.id}
                  id={column.id}
                  title={column.title}
                >
                  {column.cards.map((card, index) => (
                    <Card
                      key={card.id}
                      id={card.id}
                      index={index}
                      text={card.text}
                      handleDeleteCard={() => handleDeleteCard(card.id)}
                    />
                  ))}
                  {provided.placeholder}
                  <div className="mt-2">
                    <input
                      type="text"
                      placeholder="Add new card"
                      value={cardTextByColumn[column.id] || ''}
                      onChange={(e) => setCardTextByColumn({
                        ...cardTextByColumn,
                        [column.id]: e.target.value
                      })}
                      className="w-full rounded border border-gray-200 p-2"
                    />
                    <button
                      onClick={() => {
                        handleAddCard(column.id, cardTextByColumn[column.id] || '')
                        setCardTextByColumn({
                          ...cardTextByColumn,
                          [column.id]: '' // Clear the input field after adding the card
                        })
                      }}
                      className="mt-2 rounded bg-blue-500 px-4 py-2 text-lg text-white hover:bg-blue-600"
                    >
              Add Card
                    </button>
                  </div>
                </Column>
              </div>
            )}
          </Droppable>
        ))}

      </div>
    </DragDropContext>
  )
}

export default TaskFlow
