// Column.js or Column.tsx
import { type FC, useState } from 'react'
import { Droppable } from 'react-beautiful-dnd'
import Modal from './Modal' // Your Modal component

export interface ColumnProps {
  id: string;
  title: string;
  children: React.ReactNode;
  onAddCard: (columnId: string, text: string) => Promise<void>; // Function to add a card
}

const Column: FC<ColumnProps> = ({ id, title, children, onAddCard }) => {
  const [isModalOpen, setIsModalOpen] = useState(false)
  const [newCardText, setNewCardText] = useState('')

  const handleAddCard = () => {
    if (newCardText.trim()) {
      onAddCard(id, newCardText).then(() => {
        setIsModalOpen(false) // Close the modal on success
        setNewCardText('') // Reset the input field
      })
    }
  }

  return (
    <>
      <h2 className="col-span-1 mb-4 max-h-4 text-lg font-bold">{title}</h2>
      <Droppable droppableId={id}>
        {(provided) => (
          <div
            ref={provided.innerRef}
            {...provided.droppableProps}
            className="space-y-2 justify-self-start"
          >
            {children}
            {provided.placeholder}
          </div>
        )}
      </Droppable>

      <button 
        onClick={() => setIsModalOpen(true)} 
        className="mb-2 place-self-end rounded bg-blue-500 px-4 py-2 text-white hover:bg-blue-600">
        Add Card
      </button>

      {isModalOpen && (
        <Modal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} onSubmit={handleAddCard}>
          <input
            type="text"
            value={newCardText}
            onChange={(e) => setNewCardText(e.target.value)}
            placeholder="Enter card text"
            className="w-full rounded border border-gray-200 p-2"
          />
        </Modal>
      )}
    </>
  )
}

export default Column
