import { FC, useState } from 'react'
import { Draggable } from 'react-beautiful-dnd'
import UsersList from './UsersList'
import IconSVG from './inlineImages'
import { User } from 'main/types'

interface CardProps {
  id: string;
  index: number;
  text: string;
  assignedUser: User | null;
  users: User[];
  onUserSelect: (cardId: string, user: User) => Promise<void>;
  onDeleteCard: () => Promise<void>;
}

const Card: FC<CardProps> = ({
  id,
  index,
  text,
  assignedUser,
  users,
  onUserSelect,
  onDeleteCard
}) => {
  const [showTooltip, setShowTooltip] = useState(false)

  return (
    <Draggable draggableId={id} index={index}>
      {(provided) => (
        <div
          ref={provided.innerRef}
          {...provided.draggableProps}
          {...provided.dragHandleProps}
          className="mb-2 flex flex-col space-y-2 rounded bg-white p-2 shadow"
        >
          <div className="flex items-center justify-between">
            <p>{text}</p>
            <div className="relative">
              <button
                onClick={() => setShowTooltip(!showTooltip)}
                className="outline-none focus:outline-none"
              >
                <IconSVG.ThreeDots />
              </button>
              {showTooltip && (
                <div className="absolute right-0 z-10 mt-2 w-40 rounded-md border border-gray-200 bg-white shadow-lg">
                  <ul className="text-sm">
                    <li>
                      <button
                        onClick={() => onDeleteCard()}
                        className="block w-full px-4 py-2 text-left hover:bg-gray-100"
                      >
                        Delete
                      </button>
                    </li>
                    {/* Add more list items/actions here */}
                  </ul>
                </div>
              )}
            </div>
          </div>
          {assignedUser && (
            <div className="flex items-center justify-between">
              <p>Assigned to: {assignedUser.name}</p>
            </div>
          )}
          <UsersList
            users={users}
            showUserList={showTooltip}
            onUserSelect={(user) => onUserSelect(id, user)}
          />
        </div>
      )}
    </Draggable>
  )
}

export default Card
